INSERT INTO event (id, title, organization, description, location, start_date, start_time, end_date, end_time, cost, contact_name, contact_email, contact_phone, url, photo, password, verified, created_at) VALUES ('b5chcgvgykoc', 'Earth Day', 'Parks Department, Marion Utilities', 'You''re invited to the 5th Anniversary of Marion''s Annual Earth Day Celebration! There will be plenty of fun things going on that will display, promote, and celebrate the Earth and doing our part as stewards of the environment!

