INSERT INTO event (id, title, organization, description, location, start_date, start_time, end_date, end_time, cost, contact_name, contact_email, contact_phone, url, photo, password, verified, created_at) VALUES ('8zjpavz59ekh', 'Community Day Obstacle Course & Fishing', 'Marion Utilities', 'Join us for our 4th Annual Community Day to help low-income families in Marion! Community Day is an awesome event for families with a NEW, fun obstacle course and free fishing at Matter Park! For a small fee, you and your family can traverse our NEW, one-of-a-kind obstacle course that includes a rope swing, tire run, zip line, water canon, and much more! Take advantage of the free activities including fishing at the Matter Park Pond, picnic, and prizes and give-aways! 

